        $('#accordion').on('shown.bs.collapse', toggleChevron);
        $('#accordion2').on('hidden.bs.collapse', toggleChevron2);
        $('#accordion2').on('shown.bs.collapse', toggleChevron2);

        var $first_item = $('#faq_menu>li:first-child');
        if ($first_item.hasClass('li-sub')) {
            $first_item.find('ul').addClass('in');
            $first_item.find('ul>li:first-child>a').addClass('side-title-check');
        } else {
            $first_item.find('a').addClass('side-title-check');
        }

        $('#faq_menu li>a').click(function (e) {
            var $parent = $(this).parent();
            if (!$parent.hasClass('li-sub')) {
                var href = $(this).attr('href');
                $('.toggle-posts.show').addClass('hidden');
                $(href).removeClass('hidden');
                $('#faq_menu li>a.side-title-check').removeClass('side-title-check');
                $parent.find('a:first-child').addClass('side-title-check');

                $("html, body").animate({scrollTop: $('.wrap-content-title').offset().top}, 600);
                e.preventDefault();   //prevent the click from jumping esp on hashes
                e.stopPropagation();

                return false;
            }

        });

    });

</script>            </div>
       
</div>
@endsection




